import React from 'react';
import { useState } from 'react';

const PoForm = () => {

  const [menu, setMenu] = useState([]);

  const addQuantity = () =>{setMenu([...menu, {value:1}])}

  const data = [
    {
      name: 'Burger',
      id: 1,
      cost: 80,
      quantity: 1
    },
    {
      name: 'Fried Chicken',
      id: 2,
      cost: 60,
      quantity: 1
    },
    {
      name: 'French Fries',
      id: 3,
      cost: 40,
      quantity: 1
    },
    {
      name: 'Pizza',
      id: 4,
      cost: 800,
      quantity: 1
    },
    {
      name: 'Pasta',
      id: 5,
      cost: 300,
      quantity: 1
    },
    {
      name: 'Fish and Chips',
      id: 6,
      cost: 160,
      quantity: 1
    },
    {
      name: 'Sandwiches',
      id: 7,
      cost: 120,
      quantity: 1
    },
    {
      name: 'Fried Rice',
      id: 8,
      cost: 270,
      quantity: 1
    },
    {
      name: 'Cheese Cake',
      id: 9,
      cost: 130,
      quantity: 1
    },

  ]

  return (
    <div className='container my-5 '>
      <div className="row card border">

        <div className="col-md-3 py-2">
          <select class="form-select" aria-label="Default select example">
            <option selected>Select item from this list</option>
            {data.map((item) => <option key={item.id} value='item.id'>{item.name}</option>)}

          </select>
          <button className='my-3 btn btn-secondary'>Add Item</button>
        </div>


        <div className="col-md-12 py-2">
          <table class="table table-hover">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Product Name</th>
                <th scope="col">Price</th>
                <th scope="col"> Quantity</th>
                <th scope="col">Total</th>
              </tr>
            </thead>

            <tbody>
              {
                data.map((item, idx) => {
                  return (
                    <tr>
                      <th scope="row">#{idx + 1}</th>
                      <td>{item.name}</td>
                      <td> ৳  <span>{item.cost}</span> </td>
                      <td>
                        <span><button type="button" class="btn btn-outline-success">+</button></span>
                        <span className='px-2'>{item.quantity}</span>
                        <span><button type="button" className='btn btn-outline-danger'>-</button></span>
                      </td>
                      <td> ৳  <span>500</span> </td>
                      <td><button className='btn text-dark float-end fw-bold '

                      >x</button>
                      </td>
                    </tr>

                  );
                })
              }
            </tbody>
          </table>
        </div>





      </div>
    </div>
  );
}

export default PoForm;
