import React from 'react';
import { useState } from 'react';


const FormDetails = () => {

  const [myName, setMyName] = useState(null);
  const [favLanguage, setFavLanguage] = useState(null);
  const [favCar, setFavCar] = useState([]);
  

  // const [favCar2, setFavCar2] = useState(null);
  // const [favCar3, setFavCar3] = useState(null); 
  const addCar = (car) => {
    if (car === auto) {
      
      setFavCar([...favCar, car, target, value])
    }
  }

  const deleteCar = (idx) => setFavCar(favCar.filter((item ,index)=> index !== idx))
  return (


    <div>
      <form action="" className='my-5'
      onSubmit={(e) => {
        e.preventDefault();
        console.log(myName)
        console.log(favLanguage);
        console.log(favCar);
        // console.log(favCar2);
        // console.log(favCar3);
      }}
      >
        <label htmlFor="firstName">First Name</label>
        <input
         type="text" 
         id='firstName'
         value={myName}
         onChange={(e) => setMyName(e.target.value)}
         />
        <br />

        <h5>Choose your favourite language</h5>
        <input
         type="radio" 
         id="html" 
         name="fav_language" 
         value="html" 
         onClick={(e) => setFavLanguage(e.target.value)}
         />
        <label htmlFor="html">HTML</label>
        <br />

        <input
         type="radio" 
         id="css" 
         name="fav_language" 
         value='css' 
         onClick={(e) => setFavLanguage(e.target.value)}
         />
        <label htmlFor="css">CSS</label>
        <br />

        <input 
        type="radio" 
        id="javascript" 
        name="fav_language" 
        value='javascript' 
        onClick={(e) => setFavLanguage(e.target.value)}
        />
        <label htmlFor="javascript">JavaScript</label>
        <br />

        <h5>Checkbox</h5>
        <input
        type="checkbox" 
        id="vehicle1" 
        name="vehicle1" 
        value="Bike" 
        onClick={addCar(auto)}
        />
        <label htmlFor="vehicle1"> I have a bike</label>
        <br />

        <input 
        type="checkbox" 
        id="vehicle2" 
        name="vehicle2" 
        value="Car" 
        onClick={(e) => setFavCar([...favCar,e.target.value])}
        />
        <label htmlFor="vehicle2"> I have a car</label>
        <br />

        <input 
        type="checkbox" 
        id="vehicle3" 
        name="vehicle3" 
        value="Boat" 
        onClick={(e) => setFavCar([...favCar,e.target.value])}
        />
        <label htmlFor="vehicle3"> I have a boat</label>
        <br />

        <input type="submit" value="submit"/>
      </form>
    </div>
  );
}

export default FormDetails;
