import React, { useState } from 'react';

const Counter = () => {
  const [count, setCount] = useState([0]);
  // const [control, setControl] = useState([0]);


  const counterHandle = (type) => {
    if (type === "addCount"){
      setCount([...count, 0])
    }

    if(type === "removeAll"){
      setCount([])
    }
  }



  const handleChange = (type) => {

    if (type === "increment") {
      // const newCount = [...count, 0]
      setCount(count(item) + 1)
    }

    if (type === "decrement") {

      setCount(count - 1)

    }

    if (type === "reset") {
      setCount(0)
    }

  }


  
  
  



  return (
    <div>
      <div>
        <button className='btn btn-secondary my-3'
         onClick={()=> counterHandle("addCount")}
         >Add</button>

        <button className='btn btn-danger mx-3'
         onClick={()=> counterHandle("removeAll")}
         >Remove All</button>

      </div>
      {count.map((item,idx)=>{
          return(

        <div className="container" key={idx}>
          <span className='badge bg-secondary'>#{idx + 1}</span>
          <div className="row my-3">
            <div className="col-md-4">
              <div className="card">
                <div className="card-header text-center">
                  <span className='fs-3'>{item}</span> 
                  <button className='btn text-danger float-end fs-3'
                  onClick={() => counterHandle('removeCard')}
                  >x</button>
                  
                </div>
                <div className="card-body text-center">
                  <button
                    className='btn btn-info'
                    onClick={() => handleChange("increment")}
                    type="button"
                  >
                    +
                  </button>
                  
                  <button
                    className='btn btn-dark mx-3'
                    onClick={() => handleChange("reset")}
                    type="button"
                  >
                    Reset
                  </button>
                  <button
                    className='btn btn-danger'
                    onClick={() => handleChange("decrement")}
                    type="button"
                  >
                    -
                  </button>

                </div>
              </div>
            </div>
          </div>       
        </div>
        ) })
      }

      

    
    </div>
  );

}

export default Counter;
