import React, { useState } from 'react';
import ControlPanel from './ControlPanel';

const Counter = () => {
  const [count, setCount] = useState([0]);
  // const [control, setControl] = useState([0]);


  const counterHandle = (type) => {
    if (type === "addCount"){
      setCount([...count, 0])
    }

    if(type === "removeAll"){
      setCount([])
    }
    if(type === 'removeCard'){
      const newCount = count.filter((item, idx)=> idx === index) 
      setCount(newCount)
    }
  }

  const handleChange = (type) => {

    if (type === "increment") {
      // const newCount = [...count, 0]
      // setCount(newCount + 1)
      console.log('increment clicked');
    }

    if (type === "decrement") {

      // setCount(count - 1)
      console.log('decrement clicked');

    }

    if (type === "reset") {
      // setCount([])
      console.log('reset clicked');
    }

  }

  return (
    <div>
      <div>
        <button className='btn btn-secondary my-3'
         onClick={()=> counterHandle("addCount")}
         >Add</button>

        <button className='btn btn-danger mx-3'
         onClick={()=> counterHandle("removeAll")}
         >Remove All</button>

      </div>
      {count.map((item,idx)=>{
          return(
                  <ControlPanel key={idx} id={idx} count={item}
                  counterHandle={counterHandle}
                  handleChange={handleChange}
                  />
        
        ) })
      }

      

    
    </div>
  );

}

export default Counter;
