import React, { useState } from 'react';
import Card from './card';



const Remort = () => {
  const [count, setCount] = useState(0);
  const [card, setcard] = useState([]);

  // const call1 = () => {
  //   const cardCopy = [...card];
  //   cardCopy.push(1);
  //   setcard(cardCopy);
  //   setCount(count + 1)
  // }

  // const call2 = () => {
  //   const cardCopy = [...card];
  //   cardCopy.pop(1);
  //   setcard(cardCopy);
  //   setCount(count - 1);
  // }
  // const handleChange = (e) => {
  //   // if (type === "increment") setCount(count + 1);
  //   // if (type === "decrement") setCount(count - 1);
  //   // if (type === "reset") setCount([])
  //   console.log(e);
  // }


  const reset = () => {
    setcard([]);
    setCount(1)

  }


  return (
    <div>
      <div className='row justify-content-center'>
        <div className='col-4 border border-primary d-flex justify-content-between p-5'>
          <div>
            <button 
              className='px-3 py-1 bg-success fs-4 text-light'
              // onClick={}
              type="increment"
              >
              +
            </button>
          </div>
          <div>
            <h1>{count}</h1>
          </div>
          <div>
            <button 
              className='px-3 py-1 bg-danger fs-4 text-light'
              onClick={() => setCount(count - 1)}
              type="decrement"
              >
              -
            </button>
          </div>
          <button 
            className='px-3 mx-2 py-1 bg-warning  fs-4 text-black'
            onClick={()=> setCount([0])}
            type="reset"
            >
            Reset
          </button>

        </div>
      </div>

      <div className="row">
        {
          card.map((item, idx) => <Card key={idx} />)
        }
      </div>
    </div>
  );
}

export default Remort;
