import { type } from '@testing-library/user-event/dist/type';
import React, { useState } from 'react';

const Counter = () => {
  const [count, setCount] = useState(0);

  // {
  //   count.map((item,idx) => {
  //     count[idx]
  //   } )
  // }

  const counterHandle = (type) => {
    if (type === 'addCount'){
      setCount(...count,[0])
      console.log(count);
    }

    if (type === 'removeAll'){
      
    }


  const handleChange = (type) => {

    if (type === "increment") {

      

      setCount(count + 1)
    }

    if (type === "decrement") {

      setCount(count - 1)

    }

    if (type === "reset") {
      setCount(0)
    }

    if (type === "maxIncrement") {
      count < 10 && setCount(count + 1)
    }

 

    if (type === "maxDecrement") {
      count > -10 && setCount(count - 1)
    }


  }

  
  }



  return (
    <div className="row">
      <div className="col-md-4">
        <div className="card">
          <div className="card-header text-center">
            <span>{count}</span>
          </div>
          <div className="card-body text-center">
            <button
              className='btn btn-info'
              onClick={() => handleChange("increment")}
              type="button"
            >
              +
            </button>
            
            <button
              className='btn btn-dark mx-3'
              onClick={() => handleChange("reset")}
              type="button"
            >
              Reset
            </button>
            <button
              className='btn btn-danger'
              onClick={() => handleChange("decrement")}
              type="button"
            >
              -
            </button>

          </div>
          <div className='text-center'>
            <button className='btn btn-info m-3'
              onClick={() => handleChange("maxIncrement")}
              type='button'
            >
              Max Increment
            </button>

            <button className='btn btn-danger m-3'
              onClick={() => handleChange("maxDecrement")}
              type='button'
            >
              Max Decrement
            </button>

          </div>
        </div>
      </div>
      <div>
        <button className='btn btn-secondary my-3'
        onClick={()=> counterHandle("addCount")}
        >Add</button>

        <button className='btn btn-danger mx-3'
        onClick={()=> counterHandle("removeAll")}
        >Remove All</button>

      </div>
    </div>
  );
}

export default Counter;
